import React, { useEffect, useState } from 'react';
import AdditionalName from './additional-name';
import PersonalForm from './personal-form';
import PersonalHistory from './personal-history';
import './forms.css'
import AddressInfo from './address-info';
import AddressEntry from './address-entry';
import ReviewPage from './review-page';
import Step1 from './step-1';
import Step2 from './step-2';
import Step3 from './step-3';

export default function MultiForm() {
    localStorage.setItem("middle",false)
    const [newNationality,setNewNationality]=useState(0)
    const [surChanged,setSurChanged]=useState(0)
    const [step, setStep] = useState(1);
    const [years,setYears]=useState(0)
    const [addressyears,setAddressYears]=useState(0)
    const[values,setValues]=useState({
        forename:"",
        dob:"",
        gender:"",
        title:"",
        nationality:"",
        country_birth:"",
        email:"",
        Middle_Name_1:"",
        Middle_Name_2:"",
        Middle_Name_3:"",
        sur_name:"",
        Country_of_Birth:"",
        town_of_birth:"",
        phone_number:"",
        birth_changed_nationality:"",
        sur_changed:"",
        other_names:"",
        new_nationality:"",
        other_names_entry:[],
        manual_address:[]

    })
    const handleChange=(e)=>{
        setValues({...values, [e.target.name]: e.target.value})
        if(e.target.name=='birth_nationality' && e.target.value!='British'){
            alert('Please note that your birth country and nationality is not same')
        }

        if(values.Middle_Name_1==="" && values.Middle_Name_2==="" && values.Middle_Name_3=="" && localStorage.getItem('middle')!='true' && e.target.name=='sur_name'){
            alert("Please ensure you provide any middle name(s), in the fields provided, if you have them. Failure to provide these extra details WILL delay your certificate.")
            localStorage.setItem('middle',true)
        }
        if(e.target.value=='Yes' && e.target.name=='sur_changed'){
            setSurChanged({...surChanged,surChanged:1 })
        }
        if(e.target.value=='No' && e.target.name=='sur_changed'){
            setSurChanged({...surChanged,surChanged:0 })
        }
        if(e.target.value=='No' && e.target.name=='other_names'){
            setStep(step + 1);
        }


    }
    const handlePersonalHistory=(event)=>{
        console.log(event.target[event.target.selectedIndex].text)
        if(event.target.text=='Yes' && event.target.name=='birth_changed_nationality'){
            alert('hell')
            setNewNationality({...newNationality,newNationality:1 })
        }

        if(event.target.text=='No' && event.target.name=='birth_changed_nationality'){
            setNewNationality({...newNationality,newNationality:0 })
        }

    }
    const handleNames=(event)=>{

    }
    const onSubmitNames = (vals) =>{
        let temp_years=0

        values.other_names_entry.map((items)=>{
            temp_years= temp_years+(items.to_year-items.from_year)
        })
        temp_years= years+(vals.to_year-vals.from_year)
        setYears(temp_years)
        setValues({...values,other_names_entry:[...values.other_names_entry,vals]})
        if(vals.other_names=='No' && years>4){
            setStep(step+1)
        }

    }
    const onSubmitAddress = (vals) =>{
        let temp_years=0
        values.manual_address.map((items)=>{
            temp_years= temp_years+(items.to_year_current_address-items.from_year_current_address)
        })
        temp_years= addressyears+(vals.to_year_current_address-vals.from_year_current_address)
        setAddressYears(temp_years)
        setValues({...values,manual_address:[...values.manual_address,vals]})
        if(addressyears>4){
            setStep(step+1)
        }
        console.log(step)

    }

    const onSubmitPersonal = (data) =>{
        setValues({...values,
            forename:data.forename,
            dob:data.dob,
            gender:data.gender,
            nationality:data.birth_nationality,
            Middle_Name_1:data.Middle_Name_1,
            Middle_Name_2:data.Middle_Name_2,
            Middle_Name_3:data.Middle_Name_3,
            country_birth:data.country_birth,
            title:data.title,
            Country_of_Birth:data.Country_of_Birth,
            email:data.email,
            town_of_birth:data.town_of_birth,
            phone_number:data.phone_number
        })

        console.log(values)

        if (step < 10) {
            setStep(step + 1);
          } 
    }
    useEffect(() => {
    }, []);


    const prevStep = () => {

      if (step > 1) {
        setStep(step - 1);
      }
    };

    const render=()=>{
        switch (step) {
            case 1:
            return (
                <div>
                <Step1 />
                <PersonalForm onSubmit={onSubmitPersonal} handleChange={handleChange}/>
                </div>)
                break;
            case 2:
            return(
                <div>
                <Step2 />
                <PersonalHistory  onSubmit={onSubmitPersonal} handleChange={handlePersonalHistory} surChanged={surChanged} nationality={newNationality} prevStep={prevStep}/>
                </div>)
                break;
            case 3:
                return(
                <div>
                <Step2 />
                <AdditionalName years={years}  onSubmit={onSubmitNames} handleChange={handleNames} prevStep={prevStep}/>
                </div>
                )
            case 4:
                return(
                    <div>
                    <Step3 />
                <AddressInfo years={years}  onSubmit={onSubmitPersonal} handleChange={handleNames} prevStep={prevStep}/>
                </div>
                )
            case 5:
                return(
                    <div>
                    <Step3 />
                <AddressEntry years={addressyears}  onSubmit={onSubmitAddress} handleChange={handleNames} prevStep={prevStep}/>
                </div>
                )
            case 6:
                return(
                <ReviewPage data={values}  onSubmit={onSubmitPersonal} prevStep={prevStep}/>)
            default:
                break;
        }
    }


  return (
      <div id='background'>
        <h1 className='space'></h1>
        <div class="container card align middle">
            <div class="card-body">
                {render()}
            </div>
        </div>
    </div>
  )
}
