const About=({bold_text,img,heading,paragraph})=>{
    const content=()=>{
        if(img==null){
            return(
                <div className="row">
                <div className="col-md-6">
                    <h6><strong>{bold_text}</strong></h6>
                    <h1>{heading}</h1>
                    {paragraph.map((para)=>(
                        <p>{para}</p>
                    ))}
                </div>
                <div className="col-md-6"><img id="image-home" src={img} /></div>
            </div>
            );
        }
        else{
            return(
                <h1>Hello</h1>
            )
        }
    }
    return (
        content()
    );
}
export default About;
