import React from 'react';
import { useFormContext } from "react-hook-form";




export default function Form(props) {
    const { register,formState: { errors } } = useFormContext();
    const change = (name) => (e) => {
        console.log(name)
    };

    const renderForm =()=>{
        switch (props.input.type) {
            case 'text':
                    return(
                        <div class="form-group" key={props.input.name}>
                        <label for={props.input.name}>{props.input.title}</label>
                        <input type={props.input.type} {...register(props.input.name, { required: props.input.require ,onChange: props.input.change})}  class="form-control form-control-sm" placeholder={props.input.placholder} required={props.input.require} />
                        <div id={props.input.name} class="form-text">{props.input.smallText}</div>
                        {console.log(errors)}
                      </div>
                    )
                case 'date':
                    return(
                        <div class="form-group">
                        <label for='date'>{props.input.title}</label>
                        <input type={props.input.type} value={props.input.value} onChange={props.input.change} name={props.input.name} {...register(props.input.name, { required: props.input.require ,onChange: props.input.change})} max={props.input.max} class="form-control form-control-sm" id="forename" placeholder={props.input.placeholder} />

                      </div>
                    )
                case 'date':
                        return(
                            <div class="form-group">
                            <label for='month'>{props.input.title}</label>
                            <input type={props.input.type} value={props.input.value} onChange={props.input.change} name={props.input.name} {...register(props.input.name, { required: props.input.require ,onChange: props.input.change})} max={props.input.max} class="form-control form-control-sm" id="forename" placeholder={props.input.placeholder} />

                          </div>
                        )
                case 'select':
                    return(
                        <div>
                        <label for={props.input.title}>{props.input.title}</label>
                        <select name={props.input.name} onChange={(evt) => change(evt)} {...register(props.input.name, { required: props.input.require,onChange: props.input.change })} className='form-select form-select-sm'>
                            {props.input.options.map((option)=>(
                                <option value={option}>{option}</option>
                            ))}
                        </select>
                        <div id={props.input.name} class="form-text">{props.input.smallText}</div>

                        </div>
                        )
                case 'email':
                    return(
                        <div class="form-group">
                            <label for={props.input.name}>{props.input.title}</label>
                                <input type={props.input.type} name={props.input.name} {...register(props.input.name, { required: props.input.require })} class="form-control form-control-sm" placeholder={props.input.placeholder} aria-describedby={props.input.name} />
                                <div id={props.input.name} class="form-text">{props.input.smallText}</div>
                        </div>
                            )


            default:
                break;
        }

    }
  return (
      renderForm()

  );
}
