import React, { useEffect, useState } from "react";
import {
  PaymentElement,
  useStripe,
  useElements
} from "@stripe/react-stripe-js";
import axios from "axios";
import { wait } from "@testing-library/user-event/dist/utils";

export default function CheckoutForm({id}) {
  const stripe = useStripe();
  const elements = useElements();
  let clientSecret

  const [message, setMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (!stripe) {
      return;
    }

    clientSecret = new URLSearchParams(window.location.search).get(
      "payment_intent_client_secret"
    );

    if (!clientSecret) {
      return;
    }
    stripe.retrievePaymentIntent(clientSecret).then(({ paymentIntent }) => {
        alert('hello')
      switch (paymentIntent.status) {
        case "succeeded":
          setMessage("Payment succeeded!");
          let payment={
              customer_id:id,
              payment_id:paymentIntent.id,
              amount:paymentIntent.amount,
              date:paymentIntent.created,
              currency:paymentIntent.currency
          }
        axios.post('http://localhost:4242/notes',payment)
          .then((data)=>{
              console.log(data)
          })
          break;
        case "processing":
          setMessage("Your payment is processing.");
          break;
        case "requires_payment_method":
          setMessage("Your payment was not successful, please try again.");
          break;
        default:
          setMessage("Something went wrong.");
          break;
      }
    });
  }, [stripe]);
  const [data]= stripe.confirmPayment({
      confirmParams:{
        mandate_data:data
      }
  })
  console.log(data)

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make sure to disable form submission until Stripe.js has loaded.
      return;
    }

    setIsLoading(true);

  };

  return (
    <form id="payment-form" onSubmit={handleSubmit}>
      <PaymentElement id="payment-element" />
      <button disabled={isLoading || !stripe || !elements} id="submit">
        <span id="button-text">
          {isLoading ? <div className="spinner" id="spinner"></div> : "Pay now"}
        </span>
      </button>
      {/* Show any error or success messages */}
      {message && <div id="payment-message">{message}</div>}
    </form>
  );
}
