import Nav from '../nav'
import axios from 'axios';
import React, {Component } from 'react';
import Filter from './filter';


export default class Stats extends Component {
  constructor(props) {
    super(props)
    this.array1=["createdAt","Name","Surname","Phone Number","Email","Address","Status"]
    this.state={
      users:[],
      users2:[],
      partners:[],
      sDate:"",
      eDate: new Date().now(),
      collection:[]
    }
  } 
  onDateChange=(event)=>{
    this.setState({sDate:event.target.value})
    let array=[]
    this.state.users2.map((user)=>{

      if(new Date(user.createdAt)>=new Date(this.state.sDate)){
        array.push(user)
      }
    })
    this.setState({
      users:array
    })
  }
  componentDidMount(){
        axios.get('http://localhost:4242/users/')
        .then(res => {
          this.setState({
            users:res.data,
            users2:res.data

          });
        })
        .catch((error) => {
          console.log(error);
        })
        axios.get('http://localhost:4242/partners/')
        .then(res => {
          this.setState({
            partners:res.data
          });
        })
        .catch((error) => {
          console.log(error);
        })
        this.state.partners.forEach((partner)=>{
          let unpaid=this.state.users.filter(user=>{
            return(
              partner._id==user.lead,
              user.payment=="Unpaid"
            )

          }).length
          let paid=this.state.users.filter(user=>{
            return(
              partner._id==user.lead,
              user.payment=="Paid"
            )

          }).length
          let temp={
            name:partner.name,
            unpaid:unpaid,
            paid:paid,
            total:paid+unpaid
          }
          this.setState({
            collection:temp
          })
        })
  }
  partnerName(partner){
    return (this.state.users.map((user)=>{
        return (<td>{partner.name}</td>)

    })
    )
  }
  paidCount(partner){
    return(
      this.state.users.filter((user)=>{
        return(
        user.lead==partner._id &&
        user.status=='Paid')
      }).length
    )}
  unpaidCount(partner){
    return Number(
      this.state.users.filter((user)=>{
        return(
        user.lead==partner._id &&
        user.status=='Unpaid')
      }).length
    )}
    total(partner){
        const paid=this.state.users.filter((user)=>{
          return Number(
          user.lead==partner._id &&
          user.status=='Paid'
          )
        }).length
        const unpaid=this.state.users.filter((user)=>{
          return(
          user.lead==partner._id &&
          user.status=='Unpaid'
          )
        }).length
        return Number(paid+unpaid)

    }
 render(){

  return (
    <>
      <Nav/>
      <div className='container'>
        <h1 style={{paddingTop:'40px', paddingBottom:'30px'}}>Stats</h1>
        <Filter sDate={this.state.sDate} eDate={this.state.eDate}  onDateChange={this.onDateChange} />
        <h6 style={{width:'5%', textAlign:'center', padding:'5px', borderRadius:'4px'}} className='text-white bg-indigo-600 bg-[length:100px_100px]'>SALES</h6>
        <br/>
          <table class="table table-bordered">
            <thead>
              <tr>
                <th scope="col">Partner Name</th>
                <th scope="col">Paid Order</th>
                <th scope="col">Unpaid Order</th>
                <th scope="col">Total Order</th>
              </tr>
            </thead>
            <tbody>
            {this.state.partners.map((partner)=>{
              return(
              <tr>
                <td>
                {this.partnerName(partner)}
                </td>
                <td>
                {this.paidCount(partner)}
                </td>
                <td>
                {this.unpaidCount(partner)}
                </td>
                <td>
                {this.total(partner)}
                </td>
              </tr>
              )
              })}
            </tbody>
            </table>
            <div className='row'>
              <div className='col'>
              <h6 style={{width:'22%', textAlign:'center', padding:'5px', borderRadius:'4px'}} className='text-white bg-indigo-600 bg-[length:100px_100px]'>PAYMENT LINKS</h6>
        <br/>

        <table class="table table-bordered">
            <thead>
              <tr>

                <th scope="col">Partner Name</th>
                <th scope="col">Paid Order</th>
                <th scope="col">Total Order</th>
                <th scope="col">Conversion</th>

              </tr>
            </thead>
            <tbody>
            {this.state.partners.map((partner)=>{
              return(
              <tr>
                <td>
                {this.partnerName(partner)}
                </td>
                <td>
                {this.paidCount(partner)}
                </td>
                <td>
                {this.total(partner)}
                </td>
                <td>
                {(this.paidCount(partner)*100)/this.total(partner)}
                %
                </td>
              </tr>
              )
              })}
            </tbody>
            </table>

              </div>
              <div className='col'>
              <h6 style={{width:'21%', textAlign:'center', padding:'5px', borderRadius:'4px'}} className='text-white bg-indigo-600 bg-[length:100px_100px]'>LEADS CHASED</h6>
        <br/>

          <table class="table table-bordered">
            <thead>
              <tr>

                <th scope="col">Partner Name</th>
                <th scope="col">Paid Order</th>
                <th scope="col">Total Order</th>
                <th scope="col">Conversion</th>

              </tr>
            </thead>
            <tbody>
            {this.state.partners.map((partner)=>{
              return(
              <tr>
                <td>
                {this.partnerName(partner)}
                </td>
                <td>
                {this.paidCount(partner)}
                </td>
                <td>
                {this.total(partner)}
                </td>
                <td>
                {(this.paidCount(partner)*100)/this.total(partner)}
                %
                </td>
              </tr>
              )
              })}
            </tbody>
            </table>

              </div>

            </div>
      </div>
</>
  )
}
}
