import React, { useEffect, useState } from 'react';
import AdditionalName from './additional-name';
import PersonalForm from './personal-form';
import PersonalHistory from './personal-history';
import './forms.css'
import AddressInfo from './address-info';
import AddressEntry from './address-entry';
import ReviewPage from './review-page';

export default function MultiForm() {
    const [newNationality,setNewNationality]=useState(0)
    const [surChanged,setSurChanged]=useState(0)
    const [step, setStep] = useState(1);
    const [years,setYears]=useState(0)
    const [addressyears,setAddressYears]=useState(0)




    const[values,setValues]=useState({
        forename:"",
        dob:"",
        gender:"",
        title:"",
        birth_nationality:"",
        country_birth:"",
        email:"",
        Middle_Name_1:"",
        Middle_Name_2:"",
        Middle_Name_3:"",
        sur_name:"",
        Country_of_Birth:"",
        town_of_birth:"",
        phone_number:"",
        birth_changed_nationality:"",
        sur_changed:"",
        other_names:"",
        new_nationality:"",
        other_names_entry:[],
        manual_address:[]

    })
    const handleChange=(e)=>{
        setValues({...values, [e.target.name]: e.target.value})

        if(values.Middle_Name_1==="" && values.Middle_Name_2==="" && values.Middle_Name_3=="" && localStorage.getItem('middle')!='true'){
            alert("Please ensure you provide any middle name(s), in the fields provided, if you have them. Failure to provide these extra details WILL delay your certificate.")
            localStorage.setItem('middle',true)
        }
        if(values.Middle_Name_1==="" && values.Middle_Name_2==="" && values.Middle_Name_3=="" && localStorage.getItem('middle')!='true'){
            alert("Please ensure you provide any middle name(s), in the fields provided, if you have them. Failure to provide these extra details WILL delay your certificate.")
            localStorage.setItem('middle',true)
        }
        if(e.target.value=='Yes' && e.target.name=='birth_changed_nationality'){
            setNewNationality({...newNationality,newNationality:1 })
        }

        if(e.target.value=='No' && e.target.name=='birth_changed_nationality'){
            setNewNationality({...newNationality,newNationality:0 })
        }
        if(e.target.value=='Yes' && e.target.name=='sur_changed'){
            setSurChanged({...surChanged,surChanged:1 })
        }
        if(e.target.value=='No' && e.target.name=='sur_changed'){
            setSurChanged({...surChanged,surChanged:0 })
        }
        if(e.target.value=='No' && e.target.name=='other_names'){
            setStep(step + 1);
        }


    }
    const handleNames=(event)=>{

    }
    const onSubmitNames = (vals) =>{
        let temp_years=0

        values.other_names_entry.map((items)=>{
            temp_years= temp_years+(items.to_year-items.from_year)
        })
        temp_years= years+(vals.to_year-vals.from_year)
        setYears(temp_years)
        setValues({...values,other_names_entry:[...values.other_names_entry,vals]})
        if(vals.other_names=='No' && years>4){
            setStep(step+1)
        }

    }
    const onSubmitAddress = (vals) =>{
        let temp_years=0
        values.manual_address.map((items)=>{
            temp_years= temp_years+(items.to_year_current_address-items.from_year_current_address)
        })
        temp_years= addressyears+(vals.to_year_current_address-vals.from_year_current_address)
        setAddressYears(temp_years)
        setValues({...values,manual_address:[...values.manual_address,vals]})
        if(addressyears>4){
            setStep(step+1)
        }
        console.log(step)

    }

    const onSubmitPersonal = () =>{
        if (step < 10) {
            setStep(step + 1);
          } 

    }
    useEffect(() => {
    }, []);


    const prevStep = () => {
      if (step > 1) {
        setStep(step - 1);
      }
    };

    const render=()=>{
        switch (step) {
            case 1:
            return (
                <div>
 <div className="w-11/12 lg:w-2/6 mx-auto">
                <div className="bg-gray-200 h-1 flex items-center justify-between">
                    <div className="w-1/3 bg-indigo-700 h-1 flex items-center">
                        <div className="bg-indigo-700 h-6 w-6 rounded-full shadow flex items-center justify-center">
                            <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-check" width={18} height={18} viewBox="0 0 24 24" strokeWidth="1.5" stroke="#FFFFFF" fill="none" strokeLinecap="round" strokeLinejoin="round">
                                <path stroke="none" d="M0 0h24v24H0z" />
                                <path d="M5 12l5 5l10 -10" />
                            </svg>
                        </div>
                    </div>
                    <div className="w-1/3 flex justify-between bg-indigo-700 h-1 items-center relative">
                        <div className="absolute right-0 -mr-2">
                            <div className="relative bg-white shadow-lg px-2 py-1 rounded mt-16 -mr-12">
                                <svg className="absolute top-0 -mt-1 w-full right-0 left-0" width="16px" height="8px" viewBox="0 0 16 8" version="1.1" xmlns="http://www.w3.org/2000/svg">
                                    <g id="Page-1" stroke="none" strokeWidth={1} fill="none" fillRule="evenodd">
                                        <g id="Progress-Bars" transform="translate(-322.000000, -198.000000)" fill="#FFFFFF">
                                            <g id="Group-4" transform="translate(310.000000, 198.000000)">
                                                <polygon id="Triangle" points="20 0 28 8 12 8" />
                                            </g>
                                        </g>
                                    </g>
                                </svg>
                                <p className="text-indigo-700 text-xs font-bold">Step 3: Analyzing</p>
                            </div>
                        </div>
                        <div className="bg-indigo-700 h-6 w-6 rounded-full shadow flex items-center justify-center -ml-2">
                            <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-check" width={18} height={18} viewBox="0 0 24 24" strokeWidth="1.5" stroke="#FFFFFF" fill="none" strokeLinecap="round" strokeLinejoin="round">
                                <path stroke="none" d="M0 0h24v24H0z" />
                                <path d="M5 12l5 5l10 -10" />
                            </svg>
                        </div>
                        <div className="bg-white h-6 w-6 rounded-full shadow flex items-center justify-center -mr-3 relative">
                            <div className="h-3 w-3 bg-indigo-700 rounded-full" />
                        </div>
                        <div className="bg-white h-6 w-6 rounded-full shadow flex items-center justify-center -mr-3 relative">
                            <div className="h-3 w-3 bg-indigo-700 rounded-full" />
                        </div>
                    </div>
                    <div className="w-1/3 flex justify-end">
                        <div className="bg-white h-6 w-6 rounded-full shadow" />
                    </div>
                </div>
            </div>
                <PersonalForm onSubmit={onSubmitPersonal} handleChange={handleChange}/>
                </div>)
                break;
            case 2:
            return(<PersonalHistory  onSubmit={onSubmitPersonal} handleChange={handleChange} surChanged={surChanged} nationality={newNationality} prevStep={prevStep}/>)
                break;
            case 3:
                return(
                <AdditionalName years={years}  onSubmit={onSubmitNames} handleChange={handleNames} prevStep={prevStep}/>)
            case 4:
                return(
                <AddressInfo years={years}  onSubmit={onSubmitPersonal} handleChange={handleNames} prevStep={prevStep}/>)
            case 5:
                return(
                <AddressEntry years={addressyears}  onSubmit={onSubmitAddress} handleChange={handleNames} prevStep={prevStep}/>)
            case 6:
                return(
                <ReviewPage years={addressyears}  onSubmit={onSubmitAddress} handleChange={handleNames} prevStep={prevStep}/>)
            default:
                break;
        }
    }


  return (
      <div id='background'>
        <h1 className='space'></h1>
        <div class="container card align middle">
            <div class="card-body">
                {render()}
            </div>
        </div>
    </div>
  )
}
