import React, { useEffect, useState } from 'react';
import AdditionalName from './additional-name';
import PersonalForm from './personal-form';
import PersonalHistory from './personal-history';

export default function MultiForm() {
    const [newNationality,setNewNationality]=useState(0)
    const [surChanged,setSurChanged]=useState(0)
    const [step, setStep] = useState(1);




    const[values,setValues]=useState({
        forename:"",
        dob:"",
        gender:"",
        title:"",
        birth_nationality:"",
        country_birth:"",
        email:"",
        Middle_Name_1:"",
        Middle_Name_2:"",
        Middle_Name_3:"",
        sur_name:"",
        Country_of_Birth:"",
        town_of_birth:"",
        phone_number:"",
        birth_changed_nationality:"",
        sur_changed:"",
        other_names:"",
        new_nationality:"",

    })
    const handleChange=(e)=>{
        setValues({...values, [e.target.name]: e.target.value})

        if(values.Middle_Name_1==="" && values.Middle_Name_2==="" && values.Middle_Name_3=="" && localStorage.getItem('middle')!='true'){
            alert("Please ensure you provide any middle name(s), in the fields provided, if you have them. Failure to provide these extra details WILL delay your certificate.")
            localStorage.setItem('middle',true)
        }
        if(values.Middle_Name_1==="" && values.Middle_Name_2==="" && values.Middle_Name_3=="" && localStorage.getItem('middle')!='true'){
            alert("Please ensure you provide any middle name(s), in the fields provided, if you have them. Failure to provide these extra details WILL delay your certificate.")
            localStorage.setItem('middle',true)
        }
        if(e.target.value=='Yes' && e.target.name=='birth_changed_nationality'){
            setNewNationality({...newNationality,newNationality:1 })
        }

        if(e.target.value=='No' && e.target.name=='birth_changed_nationality'){
            setNewNationality({...newNationality,newNationality:0 })
        }
        if(e.target.value=='Yes' && e.target.name=='sur_changed'){
            setSurChanged({...surChanged,surChanged:1 })
        }
        if(e.target.value=='No' && e.target.name=='sur_changed'){
            setSurChanged({...surChanged,surChanged:0 })
        }


    }
    const onSubmitPersonal = () =>{
        console.log(values);
        if (step < 3) {
            setStep(step + 1);
          } 

    }
    useEffect(() => {


    }, []);

    const onSubmitHistory = (data) =>{
        console.log(data.forename);
        setValues({...values,
            forename: data.forename,
            dob:data.dob,
            gender:data.gender,
            title:data.title,
            birth_nationality:data.birth_nationality,
            country_birth:data.country_birth,
            email:data.email,
            Middle_Name_1:data.Middle_Name_1,
            Middle_Name_2:data.Middle_Name_2,
            Middle_Name_3:data.Middle_Name_3,
            sur_name:data.sur_name,
            Country_of_Birth:data.Country_of_Birth,
            town_of_birth:data.town_of_birth,
            phone_number:data.phone_number,
        })
        setValues((state) => {
            return state;
          });
    }

    const prevStep = () => {
      if (step > 1) {
        setStep(step - 1);
      }
    };

    const render=()=>{
        switch (step) {
            case 1:
            return (<PersonalForm onSubmit={onSubmitPersonal} handleChange={handleChange}/>)
                break;
            case 2:
            return(
            <PersonalHistory  onSubmit={onSubmitPersonal} handleChange={handleChange} surChanged={surChanged} nationality={newNationality} prevStep={prevStep}/>)
                break;
            case 3:
                return(
                <AdditionalName  onSubmit={onSubmitPersonal} handleChange={handleChange} surChanged={surChanged} nationality={newNationality} prevStep={prevStep}/>)
            default:
                break;
        }
    }


  return <div>
      {render()}
        </div>;
}
