import Nav from './nav';
import axios from 'axios';
import React, {useState,useEffect } from 'react';
import Row from './components/row';
import Box from './components/box';
import Thead from './components/thead';

export default function Dashboard() {
  var paid=0
  const [users,setUsers]=useState([])
  useEffect( () => {

    axios.get('http://localhost:4242/users/')
    .then(res => {
      setUsers(
        res.data
      );
      console.log(users)
    })
    .catch((error) => {
      console.log(error);
    })
    paid=users.filter(item=>{
      if(item.status=='paid'){
        return true
      }
      return false
    }).length;

  }, [])
  const deleteStudent=(id)=> {
    try{
      const updatedUsers=users.filter((user)=>user._id!=id)
      setUsers(updatedUsers)
      axios.delete('http://localhost:4242/delete-user/' + id)
      .then((res) => {
          console.log('User successfully deleted!')
      }).catch((error) => {
          console.log(error)
      })
    }
    catch{
      console.error("User not deleted");
    }

}
const array1=["createdAt","Name","Surname","Phone Number","Email","Address","Status"]
  return <>
  <Nav />
  <div>
  <div class="bg-gray-100">
  <div class="max-w-7xl mx-auto py-12 px-4 sm:px-6 lg:px-8">

<div>
  <h3 class="text-lg leading-6 font-medium text-gray-900">
    Last 30 days
  </h3>
  <dl class="mt-5 grid grid-cols-1 gap-5 sm:grid-cols-3">
    <Box data={users.length} title={"Total Users"} />
    <Box data={paid} title={"Paid Users"} />
    <Box data={users.length-paid} title={"Unpaid Users"} />

  </dl>
</div>

  </div>
</div>
<div class="bg-gray-100 py-6">
    <div class="max-w-7xl mx-auto sm:px-6 lg:px-8">
      <h6>TOTAL USERS</h6>
  <div class="flex flex-col">
    <div class="-my-2 overflow-x-auto sm:-mx-6 lg:-mx-8">
      <div class="py-2 align-middle inline-block min-w-full sm:px-6 lg:px-8">
        <div class="shadow overflow-hidden border-b border-gray-200 sm:rounded-lg">
          <table class="min-w-full divide-y divide-gray-200">
            <Thead headings={array1} />
            <tbody class="bg-white divide-y divide-gray-200" x-max="1">
              {users.map((user)=>{
                 return <Row user={user} deleteStudent={deleteStudent} />


              })}

            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>

    </div>
  </div>
  </div>
  </>
}
