import Nav from './nav';
import axios from 'axios';
import React, {useState,useEffect } from 'react';
import Row from './components/row';
import Box from './components/box';
import { Component } from 'react/cjs/react.production.min';

export default class Dashboard extends Component {
  constructor(props) {
    var paid=0
    this.state={
      users:[]
    }

  }
  deleteStudent=(id)=> {
    try{
      const updatedUsers=this.users.filter((user)=>user._id!=id)
      this.setState={
        users:updatedUsers
      }
      axios.delete('http://localhost:4242/delete-user/' + id)
      .then((res) => {
          console.log('User successfully deleted!')
      }).catch((error) => {
          console.log(error)
      })
    }
    catch{
      console.error("User not deleted");
    }

}
componentDidMount=()=>{
  axios.get('http://localhost:4242/users/')
    .then(res => {
      this.setState={
        users:res.data
      }
    })
    .catch((error) => {
      console.log(error);
    })
    this.paid=this.users.filter(item=>{
      if(item.status=='paid'){
        return true
      }
      return false
    }).length;

}
render(){


  return <>
  <Nav />
  <div style={{height:'100vh'}}>
  <div class="bg-gray-100" >
  <div class="max-w-7xl mx-auto py-12 px-4 sm:px-6 lg:px-8">

<div>
  <h3 class="text-lg leading-6 font-medium text-gray-900">
    Last 30 days
  </h3>
  <dl class="mt-5 grid grid-cols-1 gap-5 sm:grid-cols-3">
    <Box data={this.state.users.length} title={"Total Users"} />
    <Box data={this.paid} title={"Paid Users"} />
    <Box data={this.state.users.length-this.paid} title={"Unpaid Users"} />
  </dl>
</div>
<table class="table table-bordered">
            <thead>
              <tr>
                <th scope="col">Partner Name</th>
                <th scope="col">Paid Order</th>
                <th scope="col">Unpaid Order</th>
                <th scope="col">Total Order</th>
              </tr>
            </thead>
            <tbody>
            {this.state.partners.map((partner)=>{
              return(
                <Row user={this.state.users} deleteStudent={this.deleteStudent} />
              )
              })}
            </tbody>
            </table>
  </div>
</div>

  </div>
  </>
}
}
