import React from 'react';

export default function Form(props) {
    const renderForm =()=>{
        switch (props.input.type) {
            case 'text':
                return(
                    <div class="form-group">
                    <label for="forename">{props.input.title}</label>
                    <input type={props.input.type} name={props.input.name} class="form-control form-control-sm" id="forename" placeholder={props.input.placeholder} />
                  </div>
                )
                case 'date':
                    return(
                        <div class="form-outline datepicker-translated">
                        <input
                            type="text"
                            class="form-control"
                            id="exampleDatepicker3"
                        />
                        <label for="exampleDatepicker3" class="form-label">Select a date</label>
                        </div>
                    )

            default:
                break;
        }

    }
  return (
      renderForm()
  );
}
