import React from 'react';
import { useForm } from "react-hook-form";


export default function Form(props) {
    const { register, handleSubmit, watch, errors } = useForm();

    let day=[""]
    for(var i=1;i<32;i++){
        day.push(i)
    }
    console.log(day)
    const renderForm =()=>{
        switch (props.input.type) {
            case 'text':
                if(props.input.required==true){
                    return(
                        <div class="form-group">
                        <label for={props.input.name}>{props.input.title}</label>
                        <input type={props.input.type} name={props.input.name} class="form-control form-control-sm" placeholder={props.input.placholder} />

                        <div id={props.input.name} class="form-text">{props.input.smallText}</div>


                      </div>
                    )

                }
                else{
                    return(
                        <div class="form-group">
                        <label for={props.input.name}>{props.input.title}</label>
                        <input type={props.input.type} name={props.input.name} class="form-control form-control-sm" placeholder={props.input.placholder} required={true} />

                        <div id={props.input.name} class="form-text">{props.input.smallText}</div>


                      </div>
                    )
                }


                case 'date':
                    return(
                        <div class="form-group">
                        <label for='date'>{props.input.title}</label>
                        <input type={props.input.type} name={props.input.name} max={props.input.max} class="form-control form-control-sm" id="forename" placeholder={props.input.placeholder} />

                      </div>
                    )
                case 'select':
                    return(
                        <div>
                        <label for={props.input.title}>{props.input.title}</label>
                        <select name={props.input.name} className='form-select form-select-sm'>
                            {props.input.options.map((option)=>(
                                <option value={option}>{option}</option>
                            ))}
                        </select>
                        </div>
                        )
                case 'email':
                    return(
                        <div class="form-group">
                            <label for={props.input.name}>{props.input.title}</label>
                                <input type={props.input.type} name={props.input.name} class="form-control form-control-sm" placeholder={props.input.placeholder} aria-describedby={props.input.name} />
                                <div id={props.input.name} class="form-text">{props.input.smallText}</div>

                        </div>
                            )



            default:
                break;
        }

    }
  return (
      renderForm()
  );
}
