import Nav from '../nav'
import axios from 'axios';
import React, {Component } from 'react';
import Date from '../components/date';


export default class Stats extends Component {
  constructor(props) {
    super(props)
    this.array1=["createdAt","Name","Surname","Phone Number","Email","Address","Status"]
    this.state={
      users:[],
      partners:[],
      collection:[]
    }
  } 
  componentDidMount(){
        axios.get('http://localhost:4242/users/')
        .then(res => {
          this.setState({
            users:res.data
          });
        })
        .catch((error) => {
          console.log(error);
        })
        axios.get('http://localhost:4242/partners/')
        .then(res => {
          this.setState({
            partners:res.data
          });
        })
        .catch((error) => {
          console.log(error);
        })
        this.state.partners.forEach((partner)=>{
          let unpaid=this.state.users.filter(user=>{
            return(
              partner._id==user.lead,
              user.payment=="Unpaid"
            )

          }).length
          let paid=this.state.users.filter(user=>{
            return(
              partner._id==user.lead,
              user.payment=="Paid"
            )

          }).length
          let temp={
            name:partner.name,
            unpaid:unpaid,
            paid:paid,
            total:paid+unpaid
          }
          this.setState({
            collection:temp
          })
        })
  }
  partnerName(partner){
    return (this.state.users.map((user)=>{
        return (<td>{partner.name}</td>)

    })
    )
  }
  paidCount(partner){
    return(
      <td>
      {this.state.users.filter((user)=>{
        return(
        user.lead==partner._id,
        user.status=='Paid')
      }).length}
    </td>
    )}
  unpaidCount(partner){
    return(
      <td>
      {this.state.users.filter((user)=>{
        return(
        user.lead==partner._id &&
        user.status=='Unpaid')
      }).length}
    </td>
    )}
    total(partner){
        const paid=this.state.users.filter((user)=>{
          return(
          user.lead==partner._id &&
          user.status=='Paid'
          )
        }).length
        const unpaid=this.state.users.filter((user)=>{
          return(
          user.lead==partner._id,
          user.status=='Unpaid'
          )
        }).length
        return <td>{paid+unpaid}</td>

    }
 render(){

  return (
    <>
      <Nav/>
      <div className='container'>
          <table class="table .table-bordered">
            <thead>
              <tr>
                <th scope="col">Partner Name</th>
                <th scope="col">Paid Order</th>
                <th scope="col">Unpaid Order</th>
                <th scope="col">Total Order</th>
              </tr>
            </thead>
            <tbody>
            {this.state.partners.map((partner)=>{
              return(
              <tr>
                {this.partnerName(partner)}
                {this.paidCount(partner)}
                {this.unpaidCount(partner)}
                {this.total(partner)}


              </tr>
              )
              })}

            </tbody>
            </table>
      </div>
</>
  )
}
}
