import React, { useState } from 'react';
import PersonalForm from './personal-form';
import PersonalHistory from './personal-history';

export default function MultiForm() {
    const [newNationality,setNewNationality]=useState(false)
    const handleChange=(e)=>{
        setValues({...values, [e.target.name]: e.target.value})
        if(values.Middle_Name_1==="" && values.Middle_Name_2==="" && values.Middle_Name_3=="" && localStorage.getItem('middle')!='true'){
            alert("Please ensure you provide any middle name(s), in the fields provided, if you have them. Failure to provide these extra details WILL delay your certificate.")
            localStorage.setItem('middle',true)
        }
        if(values.Middle_Name_1==="" && values.Middle_Name_2==="" && values.Middle_Name_3=="" && localStorage.getItem('middle')!='true'){
            alert("Please ensure you provide any middle name(s), in the fields provided, if you have them. Failure to provide these extra details WILL delay your certificate.")
            localStorage.setItem('middle',true)
        }
        if(values.birth_changed_nationality=='Yes'){
            alert('hello')
            setNewNationality({...values,newNationality:true })
        }

    }

    const[values,setValues]=useState({
        forename:"",
        dob:"",
        gender:"",
        title:"",
        birth_nationality:"",
        country_birth:"",
        email:"",
        Middle_Name_1:"",
        Middle_Name_2:"",
        Middle_Name_3:"",
        sur_name:"",
        Country_of_Birth:"",
        town_of_birth:"",
        phone_number:"",
        birth_changed_nationality:"",
        sur_changed:"",
        other_names:"",
        new_nationality:"",

    })

    const onSubmitPersonal = () =>{
        console.log(values);
        if (step < 3) {
            setStep(step + 1);
          } 

    } 
    const onSubmitHistory = (data) =>{
        console.log(data.forename);
        setValues({...values,
            forename: data.forename,
            dob:data.dob,
            gender:data.gender,
            title:data.title,
            birth_nationality:data.birth_nationality,
            country_birth:data.country_birth,
            email:data.email,
            Middle_Name_1:data.Middle_Name_1,
            Middle_Name_2:data.Middle_Name_2,
            Middle_Name_3:data.Middle_Name_3,
            sur_name:data.sur_name,
            Country_of_Birth:data.Country_of_Birth,
            town_of_birth:data.town_of_birth,
            phone_number:data.phone_number,
        })
        console.log(values);

    }
    const [step, setStep] = useState(1);

    const prevStep = () => {
      if (step > 1) {
        setStep(step - 1);
      }
    }; 
    const render=()=>{
        switch (step) {
            case 1:
            return (<PersonalForm onSubmit={onSubmitPersonal} handleChange={handleChange}/>)
                break;
            case 2:
            return(<PersonalHistory onSubmit={onSubmitHistory} handleChange={handleChange} nationality={newNationality} prevStep={prevStep}/>)
                break;

            default:
                break;
        }
    }


  return <div>
      {render()}
        </div>;
}
