import React from 'react';

export default function Form(props) {
    let day=[""]
    for(var i=1;i<32;i++){
        day.push(i)
    }
    console.log(day)
    const renderForm =()=>{
        switch (props.input.type) {
            case 'text':
                return(
                    <div class="form-group">
                    <label for="forename">{props.input.title}</label>
                    <input type={props.input.type} name={props.input.name} class="form-control form-control-sm" id="forename" placeholder={props.input.placeholder} />
                  </div>
                )
                case 'date':
                    return(
                        <div class="form-group">
                        <label for='date'>{props.input.title}</label>
                        <input type={props.input.type} name={props.input.name} max={props.input.max} class="form-control form-control-sm" id="forename" placeholder={props.input.placeholder} />
                      </div>
                    )

            default:
                break;
        }

    }
  return (
      renderForm()
  );
}
