import Nav from '../nav'
import axios from 'axios';
import React, {Component } from 'react';
import Date from '../components/date';

export default class Stats extends Component {
  constructor(props) {
    super(props)
    this.array1=["createdAt","Name","Surname","Phone Number","Email","Address","Status"]
    this.state={
      users:[],
      partners:[],
      collection:[]
    }
  } 
  componentDidMount(){
        axios.get('http://localhost:4242/users/')
        .then(res => {
          this.setState({
            users:res.data
          });
        })
        .catch((error) => {
          console.log(error);
        })
        axios.get('http://localhost:4242/partners/')
        .then(res => {
          this.setState({
            partners:res.data
          });
        })
        .catch((error) => {
          console.log(error);
        })
        this.state.partners.forEach((partner)=>{
          let unpaid=this.state.users.filter(user=>{
            return(
              partner._id==user.lead,
              user.payment=="Unpaid"
            )

          }).length
          let paid=this.state.users.filter(user=>{
            return(
              partner._id==user.lead,
              user.payment=="Paid"
            )

          }).length
          let temp={
            name:partner.name,
            unpaid:unpaid,
            paid:paid,
            total:paid+unpaid
          }
          this.setState({
            collection:temp
          })
        })
  }

 render(){

  return (
    <>
      <Nav/>
      <div>
        {
          this.state.collection.map(()=>{
            return <h1>Hee</h1>
          })
        }
      </div>
</>
  )
}
}
